public class Practice3 {

	public static void main(String[] args) {
		int r,c,sp,n=4;
		for(r=1;r<=n;r++)
		{
			for(sp=n-1;sp>=r;sp--)
			System.out.print(" ");
			for(c=1;c<=1;c++)
				System.out.print("*");
			System.out.print("\n");
		}
		// TODO Auto-generated method stub

	}

}
